package com.yolo.leetcode._0438_find_all_anagrams_in_a_string;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.stream.Stream;


public class FindAllAnagramsInAString_2 {


    /**
     * 滑动窗口
     */
    class Solution {
        public List<Integer> findAnagrams(String s, String p) {

            Map<Character, Integer> need = new HashMap<>();
            Map<Character, Integer> window = new HashMap<>();

            // 初始化
            for (int i = 0; i < 26; i++) {
                need.put((char) ('a' + i), 0);
                window.put((char) ('a' + i), 0);
            }

            int left = 0, right = 0;
            int valid = 0;

            List<Integer> ans = new ArrayList<>();

            // 记录p每个字符出现的次数
            for (int i = 0; i < p.length(); i++) {
                int count = need.get(p.charAt(i));
                need.put(p.charAt(i), count + 1);
            }

            // s中的有效的字符数
            long validNeed = 0;
            validNeed = need.values().stream().filter(i -> i != 0).count();

            while (right < s.length()) {
                // c 是将移入窗口的字符
                char c = s.charAt(right);
                // 右移窗口
                right++;
                // 进行窗口内数据的一系列更新
                window.put(c, window.get(c) + 1);
                if (need.get(c).equals(window.get(c))) {
                    valid++;
                }

                // 判断左侧窗口是否要收缩
                while (right - left >= p.length()) {
                    char d = s.charAt(left);
                    left++;

                    if (valid == validNeed) {
                        ans.add(left - 1);
                    }
                    if (window.get(d).equals(need.get(d))) {
                        valid--;
                    }
                    window.put(d, window.get(d) - 1);
                }


            }

            return ans;

        }


    }

    public static void main(String[] args) {
        Solution solution = new FindAllAnagramsInAString_2().new Solution();

        String s = "";
        String p = "";
        Integer a = 12;
        Integer b = 12;
        System.out.println(a == b);
        List<Integer> anagrams = solution.findAnagrams(s, p);
        for (Integer anagram : anagrams) {
            System.out.println(anagram);
        }


    }

}
